import { useState, useEffect } from "react";
import Button from "../generic/Button/Button";
import Screen from "../generic/Screen";
import Panel from "../generic/Panel";

const Countdown = () => {
    // Store the time and button
    const [isRunning, setIsRunning] = useState(null);
    const [time, setTime] = useState(0);

    // Credit for useEffect & math.floor:
    https://medium.com/how-to-react/simple-way-to-create-a-stopwatch-in-react-js-bcc0e08e041e
    useEffect(() => {
        let intervalId;
        if (isRunning) {
          // setting time from 0 to 1 every 10 milisecond using javascript setInterval method
          intervalId = setInterval(() => setTime(time + 1), 10);
        }
        return () => clearInterval(intervalId);
      }, [isRunning, time]);

     // Hours calculation
    const hours = Math.floor(time / 360000);

    // Minutes calculation
    const minutes = Math.floor((time % 360000) / 6000);

    // Seconds calculation
    const seconds = Math.floor((time % 6000) / 100);

    // Milliseconds calculation
    const milliseconds = time % 100;

    // Start and stop timer
    const startStop = () => {
        if (isRunning) {
            setIsRunning(null)
        } else {
            setIsRunning(true)
        }
    }

    // Reset timer
    const reset = () => {
        setTime(0);
    };

    return (
    <div>
        <Screen 
            hours={hours}
            minutes={minutes}
            seconds={seconds}
            milliseconds={milliseconds}/>
        <div className="grid-container">
            <Panel background-color="blue">
                <Button 
                    text={isRunning ? "Stop" : "Start"}
                    onClick={startStop}/>
                <Button 
                    text="Reset"
                    onClick={reset}/>
            </Panel>
        </div>
    </div>
    )
};

export default Countdown;
