import React from 'react'
import { useState, useEffect, useContext } from "react";
import DisplayTime from "../generic/DisplayTime/DisplayTime";
import { TimerContext } from '../../TimerProvider';

const Timer = ({minutes, seconds, id, status, rounds, type, description, work, rest}) => {
    const {isRunning, nextTimer, isReset} = useContext(TimerContext)
    const [time, setTime] = useState(minutes+seconds);
    const [roundsCount, setRoundsCount] = useState(rounds)
    const [workStatus, setWorkStatus] = useState(true)

    useEffect(() => {
        let intervalId

        // Stopwatch
        if (type === "stopwatch") {
            if (isRunning && status === "running" && time >= 0) {
                intervalId = setTimeout(() => {setTime(time + 1)}, 8);
            }

            if (isRunning && time === (minutes+seconds) && status === "running") {
                nextTimer()
                setTime(0)
            }

            else if (status === "complete" || isReset){
                setTime(0)
            }
        }

        // Countdown
        if (type === "countdown") {
            if (isRunning && status === "running" && time > 0) {
                intervalId = setTimeout(() => {setTime(time - 1)}, 8);
            }

            else if (isRunning && time === 0 && status === "running") {
                nextTimer()
                setTime(minutes+seconds)
            }
            else if (status === "complete" || isReset){
                setTime(minutes+seconds)
            }
        }

        // XY
        if (type === "countdown") {
            if (isRunning && status === "running" && time > 0) {
                // setting time from 0 to 1 every 10 millisecond using javascript setInterval method
                intervalId = setTimeout(() => setTime(time - 1), 8);
                }
            else if (isRunning && status === "running" && time === 0 && rounds >= 1) {
                setTime(minutes+seconds+99)
                setRoundsCount(roundsCount-1)
                intervalId = setTimeout(() => setTime(time - 1), 8);
            }
            if (roundsCount === 0){
                nextTimer()
                setTime(minutes+seconds)
                setRoundsCount(rounds)
            }
            else if (status === "complete" || isReset){
                setTime(minutes+seconds)
                setRoundsCount(rounds)
            }
        }

        // Tabata
        if (type === "tabata") {
            if (isRunning && status === "running" && time !==0 && roundsCount > 0) {
                // setting time from 0 to 1 every 10 millisecond using javascript setInterval method
                intervalId = setTimeout(() => setTime(time - 1), 8);
                }

            if (isRunning && status === "running" && workStatus && time === 0 && roundsCount > 0) {
                setTime(rest+99)
                setWorkStatus(null)
                intervalId = setTimeout(() => setTime(time - 1), 8);
            }

            if (isRunning && status === "running" && workStatus === null && time === 0 && roundsCount > 0) {
                setTime(work+99 )
                setWorkStatus(true)
                setRoundsCount(roundsCount-1) 
                intervalId = setTimeout(() => setTime(time - 1), 8);
            }

            else if(roundsCount === 0){
                nextTimer()
                setTime(work)
                setRoundsCount(rounds)
                setWorkStatus(true)
            }

            else if (status === "complete" || isReset){
                setTime(work)
                setRoundsCount(rounds)
                setWorkStatus(true)
            }
        }
        return () => clearTimeout(intervalId);

    }, [isRunning,
        type, 
        nextTimer, 
        status, 
        time, 
        minutes, 
        seconds, 
        rounds, 
        roundsCount, 
        isReset,  
        rest, 
        work, 
        workStatus])

     // Minutes calculation
     const minutesCalc = Math.floor((time % 360000) / 6000);

     // Seconds calculation
     const secondsCalc = Math.floor((time % 6000) / 100);

     return (
        <div className="grid-container">
            {description !== '' ? <h3>{description}</h3> : ''}
            {type}: {minutes/6000}m{seconds/100}s
            {type === "tabata" || type === "xy" ? <div>Rounds: {rounds}</div>: ''}
            {type === "tabata" || type === "xy" ? <div>Rounds remaining: {roundsCount}</div>: ''}
            {/* <CountdownSelect /> */}
            <DisplayTime
                minutes={minutesCalc}
                seconds={secondsCalc}
                displayStyle={status === "running" ? "Default-display Running" : (status === "complete" ? "Default-display Complete" : "Default-display")}/>
        </div>
    )

}

export default Timer;
