import { useState, useEffect } from "react";
import Button from "../generic/Button/Button";
import DisplayTime from "../generic/DisplayTime/DisplayTime";
import Panel from "../generic/Panel";
import Selector from "../generic/Selector";

const XY = () => {
    // Store the time and button
    const [isRunning, setIsRunning] = useState(null);
    const [time, setTime] = useState(0);
    const [min, setMinutes] = useState(null);
    const [sec, setSeconds] = useState(null);
    const [rounds, setRounds] = useState(0);

    useEffect(() => {
        let intervalId;
        for (var i = 0; i < rounds; i++) {
            if (isRunning && time != 0) {
                // setting time from 0 to 1 every 10 millisecond using javascript setInterval method
                intervalId = setInterval(() => setTime(time - 1), 10);
              }
            else if (isRunning && time === 0) {
                setTime(min+sec)
                setRounds(rounds-1)
                intervalId = setInterval(() => setTime(time - 1), 10);
            }
            console.log(rounds, time, i)
            return () => clearInterval(intervalId);
        } 

      }, [isRunning, time, min, sec, rounds]);



    // Minutes calculation
    const minutes = Math.floor((time % 360000) / 6000);

    // Seconds calculation
    const seconds = Math.floor((time % 6000) / 100);

    // Start and stop timer
    const startStop = () => {
        if (isRunning) {
            setIsRunning(null)
        } else {
            setIsRunning(true)
            setTime(min + sec)
        }
    }

    const reset = () => {
        setIsRunning(null);
        setTime(min + sec)
        setRounds(rounds)
    }

    // Show 59 numbers for minutes and seconds
    const timeNumbersList = [...Array(60).keys()]
    const timeNumbers = timeNumbersList.map(number =>
        <option value={number} key={number}>{number}</option>)

    // Show max 30 rounds
    const roundNumbersList = [...Array(31).keys()]
    const roundNumbers = roundNumbersList.map(number =>
        <option value={number} key={number}>{number}</option>)

    return (
    <div>
        <Selector 
            label="min"
            onChange={e => setMinutes(e.target.value*6000)}
            numbers={timeNumbers}/>
        <Selector 
            label="sec"
            onChange={e => setSeconds(e.target.value*100)}
            numbers={timeNumbers}/>
        <Selector 
            label="rounds"
            onChange={e => setRounds(e.target.value)}
            numbers={roundNumbers}/>
        <DisplayTime
            minutes={minutes}
            seconds={seconds}/>
        <div className="grid-container">
            <Panel background-color="blue">
                <Button 
                    text={isRunning && time != 0 ? "Pause" : "Start"}
                    onClick={startStop}/>
                <Button 
                    text="Reset"
                    onClick={reset}/>
                <Button 
                    text=">>"
                    onClick={() => setTime(0)}/>
            </Panel>
        </div>
    </div>
    )
};

export default XY;
