import React, { useContext } from "react";
import styled from "styled-components";
import { TimerContext } from "../TimerProvider";
import { Link } from "react-router-dom";

import Button from "../components/generic/Button/Button";
import Stopwatch from "../components/timers/Stopwatch";
import Countdown from "../components/timers/Countdown";
import XY from "../components/timers/XY";
import Tabata from "../components/timers/Tabata";

const Timers = styled.div`
  display: grid;
  align-items: center;
  grid-template-columns: auto auto;
  justify-content: center;
`;

const Timer = styled.div`
  border: 1px solid gray;
  padding: 20px;
  margin: 10px;
  font-size: 1.5rem;
`;

const TimerTitle = styled.div``;


const TimersView = () => { 
  const {timers} = useContext(TimerContext)

  return (
    <Timers>
      {/* If there are no timers, display message */}
      {/* {timers.length < 1 && <h2>No timers yet. Add a timer to get started.</h2>} */}

      <div><Link to="/add"><Button text="Add timer"/></Link></div>

      {/* Display timers */}
      {timers.map((timer) => (
        <Timer key={`timer-${timer.title}`}>
          <TimerTitle>{timer.title}</TimerTitle>
          {timer.type === "stopwatch" && <Stopwatch 
                                          minutes={timer.minutes} 
                                          seconds={timer.seconds} />}
          {timer.type === "countdown" && <Countdown 
                                          minutes={timer.minutes} 
                                          seconds={timer.seconds}/>}
          {timer.type === "xy" && <XY 
                                    minutes={timer.minutes} 
                                    seconds={timer.seconds}
                                    rounds={timer.rounds}/>}

          {timer.type === "tabata" && <Tabata 
                                        work={timer.work} 
                                        rest={timer.rest}
                                        rounds={timer.rounds}/>}
        </Timer>
      ))}
    </Timers>
  );
};

export default TimersView;
