import { useState, useEffect } from "react";
import Button from "../generic/Button/Button";
import DisplayTime from "../generic/DisplayTime/DisplayTime";
import Panel from "../generic/Panel";
import TimeSelector from "../generic/TimeSelect";

const Countdown = () => {
    // Store the time and button
    const [isRunning, setIsRunning] = useState(null);
    const [time, setTime] = useState(null);
    const [min, setMinutes] = useState(null);
    const [sec, setSeconds] = useState(null);

    const originalTime = () => {
            const totalTime = (min + sec)
            setTime(totalTime)
        }

    useEffect(() => {
        // const changeMinutes = e => {
        //     console.log(e.target.value)
        //     const minInput = (e.target.value) * 6000
        //     console.log(minInput)
        //     setMinutes(minInput)
        //     console.log(min)
        // }

        // const changeSeconds = e => {
        //     console.log(e.target.value)
        //     const secInput = (e.target.value) * 100
        //     console.log(secInput)
        //     setSeconds(secInput)
        //     console.log(sec)
        // }

        // const totalTime = (min + sec)
        // setTime(totalTime)

        // const originalTime = () => {
        //     const totalTime = (min + sec)
        //     setTime(totalTime)
        // }

        let intervalId;
        if (isRunning && time != null) {
          // setting time from 0 to 1 every 10 millisecond using javascript setInterval method
          intervalId = setInterval(() => setTime(time - 1), 10);
        }
        return () => clearInterval(intervalId);
      }, [isRunning, time, min, sec]);

     // Minutes calculation
     const minutes = Math.floor((time % 360000) / 6000);

     // Seconds calculation
     const seconds = Math.floor((time % 6000) / 100);

    // Start and stop timer
    const startStop = () => {
        if (isRunning) {
            setIsRunning(null)
        } else {
            setIsRunning(true)
        }
    }

    const reset = () => {
        setIsRunning(null);
        setTime(min + sec)
    }

    return (
    <div>
        <TimeSelector 
            label="min"
            onChange={e => setMinutes(e.target.value*6000)}/>
        <TimeSelector 
            label="sec"
            onChange={e => setSeconds(e.target.value*100)}/>
        <DisplayTime
            minutes={minutes}
            seconds={seconds}/>
        <div className="grid-container">
            <Panel background-color="blue">
                <Button 
                    text={isRunning && time != 0 ? "Pause" : "Start"}
                    onClick={startStop}/>
                <Button 
                    text="Reset"
                    onClick={reset}/>
                <Button 
                    text=">>"
                    onClick={() => setTime(0)}/>
            </Panel>
        </div>
    </div>
    )
};

export default Countdown;
