import React from 'react'
import { useContext } from 'react'
import { useState } from "react";
import Button from "../generic/Button/Button";
import Selector from "../generic/Selector/Selector";
import { TimerContext } from '../../TimerProvider';

const TabataSelect = () => { 

    const {timers, setTimers} = useContext(TimerContext)
    const [work, setWork] = useState(0)
    const [rest, setRest] = useState(0)
    const [rounds, setRounds] = useState(0)

    // Show 59 numbers for minutes and seconds
    const timeNumbersList = [...Array(60).keys()]
    const timeNumbers = timeNumbersList.map(number =>
        <option value={number} key={number}>{number}</option>)

    // Show max 30 rounds
    const roundNumbersList = [...Array(31).keys()]
    const roundNumbers = roundNumbersList.map(number =>
        <option value={number} key={number}>{number}</option>)

    return (
        <div className="grid-container">
            <Selector 
                label="sec"
                onChange={e => setWork(e.target.value*100)}
                numbers={timeNumbers}/>
            <Selector 
                label="sec"
                onChange={e => setRest(e.target.value*100)}
                numbers={timeNumbers}/>
            <Selector 
                label="rounds"
                onChange={e => setRounds(e.target.value*100)}
                numbers={roundNumbers}/>
            <Button 
                text={"Add"}
                onClick={() => setTimers(
                    [...timers, 
                    {seconds1: seconds1,
                    seconds2: seconds2,
                    rounds: rounds,
                    type: "tabata"}])}/>
        </div>
    )
};

export default TabataSelect;
