import { useState, useEffect } from "react";
import Button from "../generic/Button/Button";
import DisplayTime from "../generic/DisplayTime/DisplayTime";
import Panel from "../generic/Panel";

const Tabata = () => {
    // Store the time and button
    const [isRunning, setIsRunning] = useState(null);
    const [time, setTime] = useState(1000);
    const [rounds, setRounds] = useState(2);

    const intervalOne = 1000
    const intervalTwo = 3000
    const originalRounds = 2

    useEffect(() => {
        for (var i = 0; i < rounds; i++ ) {
            let intervalId;
            if (isRunning && time != 0) {
                // setting time from 0 to 1 every 10 milisecond using javascript setInterval method
                intervalId = setInterval(() => setTime(time - 1), 10);
                return () => clearInterval(intervalId);
            }
            setTime(originalTime);
            console.log(time, rounds, originalTime, originalRounds, i)
        }

    }, [isRunning, time, rounds]);

     // Minutes calculation
     const minutes = Math.floor((time % 360000) / 6000);

     // Seconds calculation
     const seconds = Math.floor((time % 6000) / 100);

    // Start and stop timer
    const startStop = () => {
        if (isRunning) {
            setIsRunning(null)
        } else {
            setIsRunning(true)
        }
    }

    const reset = () => {
        setIsRunning(null);
        setTime(originalTime);
        setRounds(originalRounds)
    }

    return (
    <div>
        <DisplayTime
            minutes={minutes}
            seconds={seconds}/>
        <div className="grid-container">
            <Panel background-color="blue">
                <Button 
                    text={isRunning && time != 0 ? "Pause" : "Start"}
                    onClick={startStop}/>
                <Button 
                    text="Reset"
                    onClick={reset}/>
                <Button 
                    text=">>"
                    onClick={() => setTime(0)}/>
            </Panel>
        </div>
    </div>
    )
};

export default Tabata;
