import React from 'react'
import { useState, useEffect } from "react";
import Button from "../generic/Button/Button";
import DisplayTime from "../generic/DisplayTime/DisplayTime";
import Panel from "../generic/Panel";
import { useContext } from 'react';
import { TimerContext } from '../../TimerProvider';
import Selector from '../generic/Selector/Selector';

const Stopwatch = ({minutes, seconds}) => {
    // Store the time and button
    const [isRunning, setIsRunning] = useState(null);
    const [time, setTime] = useState(0);
    const [min, setMinutes] = useState(0);
    const [sec, setSeconds] = useState(0);

    // const {isRunning, setIsRunning, time, setTime } = useContext(TimerContext)
    // const {min, setMinutes, sec, setSeconds } = useContext(TimerContext)

    // Credit for setInterval & math.floor:
    // https://medium.com/how-to-react/simple-way-to-create-a-stopwatch-in-react-js-bcc0e08e041e



    console.group()
    console.log('sec', sec)
    console.log('seconds', seconds)
    console.log('min', min)
    console.log('minutes', minutes)
    console.groupEnd()

    useEffect(() => {
        let intervalId;
        if (isRunning && min <= minutes && sec <= seconds) {
          // setting time from 0 to 1 every 10 milisecond using javascript setInterval method
          intervalId = setInterval(() => setTime(time + 1), 7);
        } else if(min === minutes && sec === seconds) {
            clearInterval(intervalId);
        }
        return () => clearInterval(intervalId);

      }, [isRunning, time, min, sec]);
    // // Minutes calculation
    const minutesCalc = Math.floor((time % 360000) / 6000);

    // Seconds calculation
    const secondsCalc = Math.floor((time % 6000) / 100);

    // Start and stop timer
    const startStop = () => {
        if (isRunning) {
            setIsRunning(null)
        } else {
            setIsRunning(true)
        }
    }

    const reset = () => {
        setIsRunning(null);
        setTime(0);
    }

    // Fast forward (clear) timer
    const fastForward = () => {
        setTime(0)
    }

    // Show 59 numbers for minutes and seconds
    const numbersList = [...Array(60).keys()]
    const numbers = numbersList.map(number =>
        <option value={number} key={number}>{number}</option>)

    return (
        <div className="grid-container">
        <Panel background-color="blue">
            <Selector 
                label="min"
                onChange={e => setMinutes(e.target.value*6000)}
                numbers={numbers}/>
            <Selector 
                label="sec"
                onChange={e => setSeconds(e.target.value*100)}
                numbers={numbers}/>
            <DisplayTime
                minutes={minutesCalc}
                seconds={secondsCalc}/>

            <Button 
                text={isRunning ? "Pause" : "Start"}
                onClick={startStop}/>
            <Button 
                text="Reset"
                onClick={reset}/>
            <Button 
                text=">>"
                onClick={fastForward}/>
        </Panel>
        </div>
    )
};

export default Stopwatch;
