import React from "react";
import { useState } from "react";

export const TimerContext = React.createContext({});

const TimerProvider = ({ children }) => {
    const [timers, setTimers] = useState([]);

    // console.log('timers', timers)

    const [isRunning, setIsRunning] = useState(null);
    // const [timerStatus, setTimerStatus] = useState(null);
    // const [workoutStatus, setWorkoutStatus] = useState(null);

    const [time, setTime] = useState(0);
    // Current running timer
    const [activeTimerIndex, setActiveTimerIndex] = useState(0); 
    // const [min, setMinutes] = useState(null);
    // const [sec, setSeconds] = useState(null);

    const nextTimer = () => {
        console.log("timers index", activeTimerIndex, "timer", timers.length)
        if (activeTimerIndex === timers.length-1) {
            timers[activeTimerIndex].status = "complete"
            setTimers(timers)
            restart()
        }
        else {
            console.log("active index", activeTimerIndex, "timers", timers)
            timers[activeTimerIndex].status = "complete"
            timers[activeTimerIndex+1].status = "running"
            setActiveTimerIndex(activeTimerIndex+1)
            setTimers(timers)
        }
    }

    // Note** add all controls to context
    // Restart timer
    const restart = () => {
        setIsRunning(null)
        setActiveTimerIndex(0)
        const resetTimers = timers.map((timer) => timer.status = "ready")
        setTimers(resetTimers)
    }

    return (
        <TimerContext.Provider
            value={{
                // functions go here
                timers,
                setTimers,
                isRunning,
                setIsRunning,
                time,
                setTime,
                activeTimerIndex,
                setActiveTimerIndex,
                nextTimer,
                restart
                // workoutStatus,
                // setWorkoutStatus
            }}
        >{children}</TimerContext.Provider>
    )
};

export default TimerProvider
