import React, { useContext, useState, useEffect } from 'react'
import Button from "../generic/Button/Button";
import Selector from "../generic/Selector/Selector";
import Input from "../generic/Input/Input";
import { TimerContext } from '../../TimerProvider';
import { makeId, minNumbers, secNumbers, timersTypes } from '../../utils/helpers';

const TimerForm = () => { 

    const {timers, setTimers, totalTime, setTotalTime} = useContext(TimerContext)
    const [minutes, setMinutes] = useState(0)
    const [seconds, setSeconds] = useState(500)
    const [clicked, setClicked] = useState(null)
    const [work, setWork] = useState(0)
    const [rest, setRest] = useState(0)
    const [type, setType] = useState('')
    const [description, setDescription] = useState('')

    useEffect(() => {
        let intervalId;

        if (clicked) {
            intervalId = setTimeout(() => {setClicked(null)}, 1000);
        }
        return () => clearTimeout(intervalId);

      }, [clicked, setClicked]);

    const handleTimer = () => {
        setTimers(
            [...timers, 
            {id: makeId(),
            description: description,
            minutes: minutes,
            seconds: seconds,
            type: "Countdown",
            status: timers.length === 0 ? "running" : "ready"}])
        setTotalTime(totalTime+minutes+seconds)
        setClicked(true)}

        console.log(timers)

    return (
        <div className="grid-container">
            <Selector></Selector>
                onChange={e => setType(e.target.value)}
                items={timersTypes}/>
            <div>
                <Input 
                    onChange={e => setDescription(e.target.value)}/>
            </div>
            <Selector 
                label="min"
                onChange={e => setMinutes(e.target.value*6000)}
                items={minNumbers}/>
            <Selector 
                label="sec"
                onChange={e => setSeconds(e.target.value*100)}
                items={secNumbers}/>
            <Button 
                text={clicked ? "Added ✓" : "Add"}
                color={clicked ? "Default-button Button-go" : "Default-button"}
                onClick={handleTimer}/>
        </div>
    )
};

export default TimerForm;
