import React from 'react'
import { useState, useEffect } from "react";
import Button from "../generic/Button/Button";
import DisplayTime from "../generic/DisplayTime/DisplayTime";
import Panel from "../generic/Panel";
import Selector from "../generic/Selector/Selector";

const XY = () => {
    // Store the time and button
    const [isRunning, setIsRunning] = useState(null);
    const [time, setTime] = useState(0);
    const [min, setMinutes] = useState(null);
    const [sec, setSeconds] = useState(null);
    const [rounds, setRounds] = useState(0);
    const [roundsCount, setRoundsCount] = useState(0)

    useEffect(() => {
        let intervalId;

        if (isRunning && time > 0) {
            // setting time from 0 to 1 every 10 millisecond using javascript setInterval method
            intervalId = setInterval(() => setTime(time - 1), 7);
            }
        else if (isRunning && time === 0 && rounds > 1) {
            setTime(min+sec+99)
            setRounds(rounds-1)
            intervalId = setInterval(() => setTime(time - 1), 7);
        }
        return () => clearInterval(intervalId);

      }, [isRunning, time, min, sec, rounds]);

    // Minutes calculation
    const minutes = Math.floor((time % 360000) / 6000);

    // Seconds calculation
    const seconds = Math.floor((time % 6000) / 100);

    // Start and stop timer
    const startStop = () => {
        if (isRunning) {
            setIsRunning(null)
        } else {
            setIsRunning(true)
        }
    }

    // Store rounds
    const handleRounds = e => {
        setRoundsCount(e.target.value)
        setRounds(e.target.value)
    }

    // Reset timer
    const reset = () => {
        setIsRunning(null);
        setTime(min + sec);
        setRounds(roundsCount)
    }

    // Fast forward (clear) timer
    const fastForward = () => {
        setTime(0)
        setRounds(0)
    }

    return (
        <div className="grid-container">
        <Panel background-color="blue">
            XY:
            <DisplayTime
                minutes={minutes}
                seconds={seconds}/>
            <Button 
                text={isRunning && time !== 0 ? "Pause" : "Start"}
                onClick={startStop}/>
            <Button 
                text="Reset"
                onClick={reset}/>
            <Button 
                text=">>"
                onClick={fastForward}/>
            </Panel>
        </div>
    )
};

export default XY;
