import { useState } from "react";
import Button from "../generic/Button/Button";
import Screen from "../generic/Screen";
import Panel from "../generic/Panel";

const Stopwatch = () => {
    // Store the time and button
    const [activeType, setActiveType] = useState(null);
    const [time, setTime] = useState(0);

    useEffect(()=> {
        let intervalId;
        if (isRunning) {
          intervalId = setInterval(() => setCount(prevCount => prevCount + 1), 1000);
        } else {
          clearInterval(intervalId)
        }
        return () => clearInterval(intervalId) // Clear after unmounting
      }, [isRunning])

    const handleClick = (type) => {
        if (type === activeType) {
          setActiveType(null);
          setisRunning(false)
        } else {
          setActiveType(type);
          setisRunning(true)
        }
    }

    return (
    <div>
        <Screen 
            value={time}/>
        <div className="grid-container">
            <Panel background-color="blue">
                <Button 
                    text="Start"
                    active={activeType === "Start"}
                    onClick={() => handleClick("Start")}/>
                <Button 
                    text="Stop"
                    active={activeType === "Stop"}
                    onClick={() => handleClick("Stop")}/>
                <Button 
                    text="Reset"
                    active={activeType === "Reset"}
                    onClick={() => handleClick("Reset")}/>
            </Panel>
        </div>
    </div>
    )
};

export default Stopwatch;
