import React, { useContext } from "react";
import styled from "styled-components";
import { TimerContext } from "../TimerProvider";
import { Link } from "react-router-dom";

import Button from "../components/generic/Button/Button";
import Stopwatch from "../components/timers/Stopwatch";
import Countdown from "../components/timers/Countdown";
import XY from "../components/timers/XY";
import Tabata from "../components/timers/Tabata";

const Timers = styled.div`
  display: grid;
  align-items: center;
  grid-template-columns: auto auto;
  justify-content: center;
`;

const Timer = styled.div`
  border: 1px solid gray;
  padding: 20px;
  margin: 10px;
  font-size: 1.5rem;
`;

const ControlButtons = styled.div`
  display: grid;
  grid-template-columns: auto;
  align-items: center;
  padding: 20px;
  margin: 10px;
  font-size: 1.5rem;
`;

const TimerTitle = styled.div``;


const TimersView = () => { 
  const {timers, isRunning, setIsRunning} = useContext(TimerContext)

  // Start or stop timer
  const startStop = () => {
    if (isRunning) {
        setIsRunning(null)
    } else {
        setIsRunning(true)
    }
}

console.log(timers[0])

  // Fast forward (clear) timer
  // const fastForward = () => {
  //   setTime(0)
  // }

  return (
    <ControlButtons>
      <Link to="/add"><Button text="+ Add timer" color={"Default-button Button-add"} /></Link><br></br>
      <Button 
        text={isRunning ? "Pause" : "Start"}
        onClick={startStop}
        color={isRunning? "Default-button Button-danger":"Default-button Button-go"} />
      <Button 
        text={"Skip >>"}
        color={"Default-button"} />

    <Timers>
      {timers.map((timer) => (
        <Timer key={`timer-${timer.title}`}>
          <TimerTitle>{timer.title}</TimerTitle>
          {timer.type === "stopwatch" && <Stopwatch 
                                          minutes={timer.minutes} 
                                          seconds={timer.seconds} />}
          {timer.type === "countdown" && <Countdown 
                                          minutes={timer.minutes} 
                                          seconds={timer.seconds}/>}
          {timer.type === "xy" && <XY 
                                    minutes={timer.minutes} 
                                    seconds={timer.seconds}
                                    rounds={timer.rounds}/>}

          {timer.type === "tabata" && <Tabata 
                                        work={timer.work} 
                                        rest={timer.rest}
                                        rounds={timer.rounds}/>}
        </Timer>
      ))}
    </Timers>
    </ControlButtons>
  );
};

export default TimersView;
