import { useState, useEffect } from "react";
import Button from "../generic/Button/Button";
import DisplayTime from "../generic/DisplayTime/DisplayTime";
import Panel from "../generic/Panel";
import Selector from "../generic/Selector";

const Tabata = () => {
    // Store the time and button
    const [isRunning, setIsRunning] = useState(null);
    const [time, setTime] = useState(0);
    const [workCount, setWorkCount] = useState(0);
    const [restCount, setRestCount] = useState(0);
    const [rounds, setRounds] = useState(0);
    const [roundsCount, setRoundsCount] = useState(0)

    useEffect(() => {
        let intervalId;

        if (isRunning && work != 0 && rounds > 1) {
            // setting time from 0 to 1 every 10 millisecond using javascript setInterval method
            setTime(workCount)
            intervalId = setInterval(() => setTime(time - 1), 10);
            }
        else if (isRunning && work === 0 && rounds > 1) {
            setTime(restCount)
            setRounds(rounds-1)
            intervalId = setInterval(() => setTime(time - 1), 10);
        }
        console.log(work, rest, rounds)
        return () => clearInterval(intervalId);



      }, [isRunning, rest, work, rounds]);

    // Seconds calculation
    const seconds = Math.floor((rest % 6000) / 100);

    // Start and stop timer
    const startStop = () => {
        if (isRunning) {
            setIsRunning(null)
        } else {
            setIsRunning(true)
            setWork(workCount)
            setRest(restCount)
        }
    }

    // Store rounds
    const handleRounds = e => {
        setRoundsCount(e.target.value)
        setRounds(e.target.value)
    }

    // Reset timer
    const reset = () => {
        setIsRunning(null);
        setWork(workCount)
        setRest(restCount)
        setRounds(roundsCount)
    }

    // Fast forward (clear) timer
    const fastForward = () => {
        setWork(0)
        setRest(0)
        setRounds(0)
    }

    // Show 59 numbers for minutes and seconds
    const timeNumbersList = [...Array(60).keys()]
    const timeNumbers = timeNumbersList.map(number =>
        <option value={number} key={number}>{number}</option>)

    // Show max 30 rounds
    const roundNumbersList = [...Array(31).keys()]
    const roundNumbers = roundNumbersList.map(number =>
        <option value={number} key={number}>{number}</option>)

    return (
    <div>
        Work
        <Selector 
            label="sec"
            onChange={e => setWorkCount(e.target.value*100)}
            numbers={timeNumbers}/><br></br>
        Rest
        <Selector 
            label="sec"
            onChange={e => setRestCount(e.target.value*100)}
            numbers={timeNumbers}/><br></br>
        <Selector 
            label="rounds"
            onChange={handleRounds}
            numbers={roundNumbers}/>
        <DisplayTime
            minutes="0"
            seconds={seconds}/>
        <div className="grid-container">
            <Panel background-color="blue">
                <Button 
                    text={isRunning && work != 0 ? "Pause" : "Start"}
                    onClick={startStop}/>
                <Button 
                    text="Reset"
                    onClick={reset}/>
                <Button 
                    text=">>"
                    onClick={fastForward}/>
            </Panel>
        </div>
    </div>
    )
};

export default Tabata;
