        : 
        <Input 
            active={true}
            placeholder="ss"
            value={secInput}
            onChange={handleSec}/>
        <DisplayTime
            minutes={minutes}
            seconds={seconds}/>
        <div className="grid-container">
            <Panel background-color="blue">
                <Button 
                    text={isRunning && time != 0 ? "Pause" : "Start"}
                    onClick={startStop}/>
                <Button 
                    text="Reset"
                    onClick={reset}/>
                <Button 
                    text=">>"
                    onClick={() => setTime(0)}/>
            </Panel>
        </div>
    </div>
    )
};

export default Countdown;
